
%function[imudata,gnsstest,gnsspre] = lstm_re(trainnum,testnum)
Data= importdata('lstm_data.mat');
inputdata=Data(:,1:13);
outputdata=Data(:,16:18);
trainnum=6300; 
testnum=6300; 
exe =trainnum-testnum+1;
%numm=trainnum+testnum;
numm=6300;
%训练数据
input_train=inputdata(1:trainnum,:)';
output_train=outputdata(1:trainnum,:)';   
[inputd,inputpstr]=mapminmax(input_train);          %输入归一化
[outputd,outputpstr]=mapminmax(output_train);  
XTrain =inputd;
YTrain =outputd;
%测试数据
[net,tr] = lstm_resss(inputd, outputd);
test_input=inputdata(trainnum:numm,:)';
test_output=outputdata(trainnum:numm,:)';
aa = test_output;
%inputn_test=mapminmax('apply',input_test,inputps); %按照inputps格式进行归一化
inputest=mapminmax('apply',test_input,inputpstr);          %输入归一化
%[outputesn,outputpns]=mapminmax(test_output);    %输出归一化
tn_sim = predict(net,inputest);
  % tn_sim = elmpredict(inputn_test,IW,B,LW,TF,TYPE);
T_sim=mapminmax('reverse',tn_sim,outputpstr);
Yt1=aa(1,:)'; Yp1=T_sim(1,:)';
Yt2=aa(2,:)'; Yp2=T_sim(2,:)';
Yt3=aa(3,:)'; Yp3=T_sim(3,:)';
%imudata=test_input;
%                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
%gnsstest=test_output;
figure;
 plot(Yt1,'r');hold on;plot(Yp1,'b');hold off;
 title('ROLL');
grid
figure;
  plot(Yt2,'r');hold on;plot(Yp2,'b');hold off;title('PITCH');
grid
figure;
   plot(Yt3,'r');hold on;plot(Yp3,'b');hold off;title('YAW');
grid
   %%
testiii=inputdata(exe:trainnum,:)';
testooo=outputdata(exe:trainnum,:)';
bb=testooo;
inputiii=mapminmax('apply',testiii,inputpstr);          %输入归一化
%[outputesn,outputpns]=mapminmax(test_output);    %输出归一化
%[net,tn_simiii]= predictAndUpdateState(net,inputiii);
tn_simiii= predict(net,inputiii);
  % tn_sim = elmpredict(inputn_test,IW,B,LW,TF,TYPE);
T_simiiii=mapminmax('reverse',tn_simiii,outputpstr);
Yt1ii=bb(1,:)'; Yp1iii=T_simiiii(1,:)';
Yt2ii=bb(2,:)'; Yp2iii=T_simiiii(2,:)';
Yt3ii=bb(3,:)'; Yp3iii=T_simiiii(3,:)';
%imudata=test_input;
%gnsstest=test_output;
 
figure;
plot(Yt1ii,'r');hold on;plot(Yp1iii,'b');hold off;
title('i1');
grid
figure;
 R2D=57.2958;
figure;
plot3(Yt1ii.*R2D, Yt2ii.*R2D, Yt3ii, '--k')
hold on
plot3(Yp1iii.*R2D,Yp2iii.*R2D, Yp3iii, '-.b')
% plot3(Yt1ii(1).*R2D,Yt2ii(1).*R2D, Yt3(1), 'or', 'MarkerSi  ze', 10, 'LineWidth', 2)
axis tight
title('TRAJECTORY')
xlabel('Longitude [deg]')
ylabel('Latitude [deg]')
zlabel('Altitude [m]')
view(0, 90)
legend('REF', 'INS/GNSS', 'Location','best');
grid
analyzeNetwork(net)
%%
function [asdasdadt] = asada(XTr, YTr)
for i=1:trainnum
 imudata.t(i,:)=inputdata(i,1);
 imudata.wb(i,:)=inputdata(i,2:4);
 imudata.fb(i,:)=inputdata(i,5:7);
 imudata.ini_al(i,:)=inputdata(i,8:10);
gnsstest.lat(i) = outputdata(i,1);
gnsstest.lon(i) = outputdata(i,2);
gnsstest.h(i) = outputdata(i,3);
gnsspre.lat(i) = outputdata(i,1);
gnsspre.lon(i) =outputdata(i,2);
gnsspre.h(i) = outputdata(i,3);
 end
 imudata.ini_align(1,:)=inputdata(1,8:10);
  gnsstest.vel(1,:)=inputdata(1,11:13);
 for i=1:testnum-1
 imudata.t(i+trainnum)=inputdata(i,1);
 imudata.wb(i+trainnum,:)=inputdata(i,2:4);
 imudata.fb(i+trainnum,:)=inputdata(i,5:7);
 imudata.ini_al(i+trainnum,:)=inputdata(i,8:10);
gnsstest.lat(i+trainnum) = Yt1(i);
gnsstest.lon(i+trainnum) = Yt2(i);
gnsstest.h(i+trainnum) = Yt3(i);
gnsspre.lat(i+trainnum) = Yp1(i);
gnsspre.lon(i+trainnum) = Yp2(i);
gnsspre.h(i+trainnum) = Yp3(i);
 end
  gnsspre.vel(1,:)=inputdata(1,11:13);


 R2D=57.2958;
figure;
plot3(Yt1.*R2D, Yt2.*R2D, Yt3, '--k');
hold on;
plot3(Yp1.*R2D,Yp2.*R2D, Yp3, '-.b');
plot3(Yt1(1).*R2D,Yt2(1).*R2D, Yt3(1), 'or', 'MarkerSize', 10, 'LineWidth', 2);
axis tight
title('TRAJECTORY')
xlabel('Longitude [deg]')
ylabel('Latitude [deg]')
zlabel('Altitude [m]')
view(0, 90)
legend('REF', 'INS/GNSS', 'Location','best');
grid

figure;
plot3(Yt1ii.*R2D, Yt2ii.*R2D, Yt3ii, '--k')
hold on
plot3(Yp1iii.*R2D,Yp2iii.*R2D, Yp3iii, '-.b')
plot3(Yt1(1).*R2D,Yt2(1).*R2D, Yt3(1), 'or', 'MarkerSize', 10, 'LineWidth', 2)
duibi.t1(:,1)=Yt1;
duibi.t2(:,1)=Yt2;
duibi.t3(:,1)=Yt3;
duibi.y1(:,1)=Yp1;
duibi.y2(:,1)=Yp2;
duibi.y3(:,1)=Yp3;
end










